import "../../core/MocCore.sol";

/**
 * @title MocCARC20: Moc Collateral Asset RC20
 * @notice Moc protocol implementation using a RC20 as Collateral Asset.
 */
contract MocCARC20 is MocCore {
    // ------- Structs -------
    struct InitializeParams {
        InitializeCoreParams initializeCoreParams;
        // Collateral Asset Token contract address
        address acTokenAddress;
    }

    // ------- Storage -------
    // Collateral Asset token
    IERC20 private acToken;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    // ------- Initializer -------
    /**
     * @notice contract initializer
     * @param initializeParams_ contract initializer params
     * @dev governorAddress The address that will define when a change contract is authorized
     *      pauserAddress The address that is authorized to pause this contract
     *      acTokenAddress Collateral Asset Token contract address
     *      tcTokenAddress Collateral Token contract address
     *      mocFeeFlowAddress Moc Fee Flow contract address
     *      mocAppreciationBeneficiaryAddress Moc appreciation beneficiary address
     *      protThrld protected state threshold [PREC]
     *      liqThrld liquidation coverage threshold [PREC]
     *      feeRetainer pct retain on fees to be re-injected as Collateral, while paying fees with AC [PREC]
     *      tcMintFee additional fee pct applied on mint Collateral Tokens operations [PREC]
     *      tcRedeemFee additional fee pct applied on redeem Collateral Tokens operations [PREC]
     *      successFee pct of the gain because Pegged Tokens devaluation that is transferred
     *        in Collateral Asset to Moc Fee Flow during the settlement [PREC]
     *      appreciationFactor pct of the gain because Pegged Tokens devaluation that is returned
     *        in Pegged Tokens to appreciation beneficiary during the settlement [PREC]
     *      emaCalculationBlockSpan amount of blocks to wait between Pegged ema calculation
     */
    function initialize(InitializeParams calldata initializeParams_) external initializer {
        acToken = IERC20(initializeParams_.acTokenAddress);
        __MocCore_init(initializeParams_.initializeCoreParams);
    }

    // ------- Internal Functions -------

    /**
     * @inheritdoc MocCore
     * @dev this function could revert during safeTransfer call.
     *  safeTransfer will revert if token transfer reverts or returns 0
     */
    function acTransfer(address to_, uint256 amount_) internal override {
        if (amount_ > 0) {
            SafeERC20.safeTransfer(acToken, to_, amount_);
        }
    }

    /**
     * @inheritdoc MocCore
     */
    function acBalanceOf(address account) internal view override returns (uint256 balance) {
        return acToken.balanceOf(account);
    }

    /**
     * @notice hook before any AC reception involving operation, as dealing with an RC20 Token
     * we need to transfer the AC amount from the user, to the contract
     * param qACMax_ max amount of AC available
     * @param qACNeeded_ amount of AC needed
     * @return change amount needed to be return to the sender after the operation is complete
     */
    function _onACNeededOperation(uint256 /*qACMax_*/, uint256 qACNeeded_) internal override returns (uint256 change) {
        SafeERC20.safeTransferFrom(acToken, msg.sender, address(this), qACNeeded_);
        // As we are transferring the exact needed amount, change is zero
        change = 0;
    }

    // ------- External Functions -------

    /**
     * @notice caller sends Collateral Asset and receives Collateral Token
        Requires prior sender approval of Collateral Asset to this contract 
     * @param qTC_ amount of Collateral Token to mint
     * @param qACmax_ maximum amount of Collateral Asset that can be spent
     * @return qACtotalNeeded amount of AC used to mint qTC
     */
    function mintTC(uint256 qTC_, uint256 qACmax_) external returns (uint256 qACtotalNeeded) {
        return _mintTCto(qTC_, qACmax_, msg.sender, msg.sender);
    }

    /**
     * @notice caller sends Collateral Asset and recipient receives Collateral Token
        Requires prior sender approval of Collateral Asset to this contract 
     * @param qTC_ amount of Collateral Token to mint
     * @param qACmax_ maximum amount of Collateral Asset that can be spent
     * @param recipient_ address who receives the Collateral Token
     * @return qACtotalNeeded amount of AC used to mint qTC
     */
    function mintTCto(uint256 qTC_, uint256 qACmax_, address recipient_) external returns (uint256 qACtotalNeeded) {
        return _mintTCto(qTC_, qACmax_, msg.sender, recipient_);
    }

    /**
     * @notice caller sends Collateral Asset and receives Pegged Token
        Requires prior sender approval of Collateral Asset to this contract 
     * @param i_ Pegged Token index to mint
     * @param qTP_ amount of Pegged Token to mint
     * @param qACmax_ maximum amount of Collateral Asset that can be spent
     * @return qACtotalNeeded amount of AC used to mint qTP
     */
    function mintTP(uint256 i_, uint256 qTP_, uint256 qACmax_) external returns (uint256 qACtotalNeeded) {
        return _mintTPto(i_, qTP_, qACmax_, msg.sender, msg.sender);
    }

    /**
     * @notice caller sends Collateral Asset and recipient receives Pegged Token
        Requires prior sender approval of Collateral Asset to this contract 
     * @param i_ Pegged Token index to mint
     * @param qTP_ amount of Pegged Token to mint
     * @param qACmax_ maximum amount of Collateral Asset that can be spent
     * @param recipient_ address who receives the Pegged Token
     * @return qACtotalNeeded amount of AC used to mint qTP
     */
    function mintTPto(
        uint256 i_,
        uint256 qTP_,
        uint256 qACmax_,
        address recipient_
    ) external returns (uint256 qACtotalNeeded) {
        return _mintTPto(i_, qTP_, qACmax_, msg.sender, recipient_);
    }

    /**
     * @notice caller sends Collateral Asset and receives Collateral Token and Pegged Token
     *  Requires prior sender approval of Collateral Asset to this contract
     *  This operation is done without checking coverage
     *  Collateral Token and Pegged Token are minted in equivalent proportions so that its price
     *  and global coverage are not modified.
     *  Reverts if qAC sent are insufficient.
     * @param i_ Pegged Token index
     * @param qTP_ amount of Pegged Token to mint
     * @param qACmax_ maximum amount of Collateral Asset that can be spent
     * @return qACtotalNeeded amount of AC used to mint Collateral Token and Pegged Token
     * @return qTCtoMint amount of Collateral Token minted
     */
    function mintTCandTP(
        uint256 i_,
        uint256 qTP_,
        uint256 qACmax_
    ) external payable returns (uint256 qACtotalNeeded, uint256 qTCtoMint) {
        return _mintTCandTPto(i_, qTP_, qACmax_, msg.sender, msg.sender);
    }

    /**
     * @notice caller sends Collateral Asset and recipient receives Collateral Token and Pegged Token
     *  Requires prior sender approval of Collateral Asset to this contract
     *  This operation is done without checking coverage
     *  Collateral Token and Pegged Token are minted in equivalent proportions so that its price
     *  and global coverage are not modified.
     *  Reverts if qAC sent are insufficient.
     * @param i_ Pegged Token index
     * @param qTP_ amount of Pegged Token to mint
     * @param qACmax_ maximum amount of Collateral Asset that can be spent
     * @param recipient_ address who receives the Collateral Token and Pegged Token
     * @return qACtotalNeeded amount of AC used to mint Collateral Token and Pegged Token
     * @return qTCtoMint amount of Collateral Token minted
     */
    function mintTCandTPto(
        uint256 i_,
        uint256 qTP_,
        uint256 qACmax_,
        address recipient_
    ) external payable returns (uint256 qACtotalNeeded, uint256 qTCtoMint) {
        return _mintTCandTPto(i_, qTP_, qACmax_, msg.sender, recipient_);
    }

    /**
     * @notice caller sends a Pegged Token and receives another one
     * @param iFrom_ owned Pegged Token index
     * @param iTo_ target Pegged Token index
     * @param qTP_ amount of owned Pegged Token to swap
     * @param qTPmin_ minimum amount of target Pegged Token that the sender expects to receive
     * @param qACmax_ maximum amount of Collateral Asset that can be spent in fees
     * @return qACFee amount of AC used to pay fee
     * @return qTPMinted amount of Pegged Token minted
     */
    function swapTPforTP(
        uint256 iFrom_,
        uint256 iTo_,
        uint256 qTP_,
        uint256 qTPmin_,
        uint256 qACmax_
    ) external returns (uint256 qACFee, uint256 qTPMinted) {
        return _swapTPforTPto(iFrom_, iTo_, qTP_, qTPmin_, qACmax_, msg.sender, msg.sender);
    }

    /**
     * @notice caller sends a Pegged Token and recipient receives another one
     * @param iFrom_ owned Pegged Token index
     * @param iTo_ target Pegged Token index
     * @param qTP_ amount of owned Pegged Token to swap
     * @param qTPmin_ minimum amount of target Pegged Token that `recipient_` expects to receive
     * @param qACmax_ maximum amount of Collateral Asset that can be spent in fees
     * @param recipient_ address who receives the target Pegged Token
     * @return qACFee amount of AC used to pay fee
     * @return qTPMinted amount of Pegged Token minted
     */
    function swapTPforTPto(
        uint256 iFrom_,
        uint256 iTo_,
        uint256 qTP_,
        uint256 qTPmin_,
        uint256 qACmax_,
        address recipient_
    ) external returns (uint256 qACFee, uint256 qTPMinted) {
        return _swapTPforTPto(iFrom_, iTo_, qTP_, qTPmin_, qACmax_, msg.sender, recipient_);
    }

    /**
     * @notice caller sends a Pegged Token and receives Collateral Token
     * @param i_ Pegged Token index
     * @param qTP_ amount of owned Pegged Token to swap
     * @param qTCmin_ minimum amount of Collateral Token that the sender expects to receive
     * @param qACmax_ maximum amount of Collateral Asset that can be spent in fees
     * @return qACFee amount of AC used to pay fee
     * @return qTPMinted amount of Collateral Token minted
     */
    function swapTPforTC(
        uint256 i_,
        uint256 qTP_,
        uint256 qTCmin_,
        uint256 qACmax_
    ) external returns (uint256 qACFee, uint256 qTPMinted) {
        return _swapTPforTCto(i_, qTP_, qTCmin_, qACmax_, msg.sender, msg.sender);
    }

    /**
     * @notice caller sends a Pegged Token and recipient receives Collateral Token
     * @param i_ Pegged Token index
     * @param qTP_ amount of owned Pegged Token to swap
     * @param qTCmin_ minimum amount of Collateral Token that `recipient_` expects to receive
     * @param qACmax_ maximum amount of Collateral Asset that can be spent in fees
     * @param recipient_ address who receives the Collateral Token
     * @return qACFee amount of AC used to pay fee
     * @return qTPMinted amount of Collateral Token minted
     */
    function swapTPforTCto(
        uint256 i_,
        uint256 qTP_,
        uint256 qTCmin_,
        uint256 qACmax_,
        address recipient_
    ) external returns (uint256 qACFee, uint256 qTPMinted) {
        return _swapTPforTCto(i_, qTP_, qTCmin_, qACmax_, msg.sender, recipient_);
    }

    /**
     * @notice caller sends Collateral Token and receives Pegged Token
     * @param i_ Pegged Token index
     * @param qTC_ amount of Collateral Token to swap
     * @param qTPmin_ minimum amount of Pegged Token that the sender expects to receive
     * @param qACmax_ maximum amount of Collateral Asset that can be spent in fees
     * @return qACFee amount of AC used to pay fee
     * @return qTPMinted amount of Pegged Token minted
     */
    function swapTCforTP(
        uint256 i_,
        uint256 qTC_,
        uint256 qTPmin_,
        uint256 qACmax_
    ) external returns (uint256 qACFee, uint256 qTPMinted) {
        return _swapTCforTPto(i_, qTC_, qTPmin_, qACmax_, msg.sender, msg.sender);
    }

    /**
     * @notice caller sends Collateral Token and recipient receives Pegged Token
     * @param i_ Pegged Token index
     * @param qTC_ amount of Collateral Token to swap
     * @param qTPmin_ minimum amount of Pegged Token that `recipient_` expects to receive
     * @param qACmax_ maximum amount of Collateral Asset that can be spent in fees
     * @param recipient_ address who receives the Pegged Token
     * @return qACFee amount of AC used to pay fee
     * @return qTPMinted amount of Pegged Token minted
     */
    function swapTCforTPto(
        uint256 i_,
        uint256 qTC_,
        uint256 qTPmin_,
        uint256 qACmax_,
        address recipient_
    ) external returns (uint256 qACFee, uint256 qTPMinted) {
        return _swapTCforTPto(i_, qTC_, qTPmin_, qACmax_, msg.sender, recipient_);
    }

    /**
     * @notice Refreshes the AC holdings for the Bucket
     * @dev Intended to be use as notification after an RC20 AC transfer to this contract
     */
    function refreshACBalance() external {
        // On this implementation, AC token balance has full correlation with nACcb
        if (acBalanceOf(address(this)) - nACcb > 0) _depositAC(acBalanceOf(address(this)) - nACcb);
    }

    /**
     * @dev This empty reserved space is put in place to allow future versions to add new
     * variables without shifting down storage in the inheritance chain.
     * See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
     */
    uint256[50] private __gap;
}
","keccak256":"0x97aec5c7009cef852a3db45ae8b1369b8f66c44f4da0a2b48c48d8adef670860"},"contracts/core/MocBaseBucket.sol":{"content":"pragma solidity ^0.8.17;

import "../interfaces/IMocRC20.sol";
import "../tokens/MocTC.sol";
import "../interfaces/IPriceProvider.sol";
import "../governance/MocUpgradable.sol";

/**
 * @title MocBaseBucket: Moc Collateral Bag
 * @notice MocBaseBucket holds Bucket Zero state, both for the Collateral Bag and PeggedTokens Items.
 * @dev Abstracts all rw operations on the main bucket and expose all calculations relative to its state.
 */
abstract contract MocBaseBucket is MocUpgradable {
    // ------- Events -------
    event ContractLiquidated();

    // ------- Custom Errors -------
    error InvalidPriceProvider(address priceProviderAddress_);
    error TransferFailed();
    error Liquidated();
    error OnlyWhenLiquidated();
    error LowCoverage(uint256 cglb_, uint256 covThrld_);
    error OnlySettlement();

    // ------- Structs -------
    struct PegContainerItem {
        // total supply of Pegged Token
        uint256 nTP;
        // PegToken PriceFeed address
        IPriceProvider priceProvider;
    }

    struct PeggedTokenIndex {
        // Pegged Token index
        uint256 index;
        // true if Pegged Token exists
        bool exists;
    }

    struct InitializeBaseBucketParams {
        // Collateral Token contract address
        address tcTokenAddress;
        // Moc Fee Flow contract address
        address mocFeeFlowAddress;
        // moc appreciation beneficiary Address
        address mocAppreciationBeneficiaryAddress;
        // protected state threshold [PREC]
        uint256 protThrld;
        // liquidation coverage threshold [PREC]
        uint256 liqThrld;
        // pct retain on fees to be re-injected as Collateral, while paying fees with AC [PREC]
        uint256 feeRetainer;
        // additional fee pct applied on mint Collateral Tokens operations [PREC]
        uint256 tcMintFee;
        // additional fee pct applied on redeem Collateral Tokens operations [PREC]
        uint256 tcRedeemFee;
        // additional fee pct applied on swap a Pegged Token for another Pegged Token [PREC]
        uint256 swapTPforTPFee;
        // additional fee pct applied on swap a Pegged Token for Collateral Token [PREC]
        uint256 swapTPforTCFee;
        // additional fee pct applied on swap Collateral Token for a Pegged Token [PREC]
        uint256 swapTCforTPFee;
        // additional fee pct applied on redeem Collateral Token and Pegged Token in one operations [PREC]
        uint256 redeemTCandTPFee;
        // additional fee pct applied on mint Collateral Token and Pegged Token in one operation [PREC]
        uint256 mintTCandTPFee;
        // pct of the gain because Pegged Tokens devaluation that is transferred
        // in Collateral Asset to Moc Fee Flow during the settlement [PREC]
        uint256 successFee;
        // pct of the gain because Pegged Tokens devaluation that is returned
        // in Pegged Tokens to appreciation beneficiary during the settlement [PREC]
        uint256 appreciationFactor;
    }

    // ------- Storage -------

    // total amount of Collateral Asset held in the Collateral Bag
    // WARN: On RC20 implementation, this correlates with contract acBalance
    uint256 public nACcb;
    // amount of Collateral Asset that the Vaults owe to the Collateral Bag
    uint256 internal nACioucb;

    // Collateral Token
    MocTC public tcToken;
    // Collateral Token in the Collateral Bag
    uint256 public nTCcb;

    // Pegged Tokens MocRC20 addresses
    IMocRC20[] public tpTokens;
    // Pegged Token indexes
    mapping(address => PeggedTokenIndex) public peggedTokenIndex;
    // peg container
    PegContainerItem[] public pegContainer;
    // Pegged Token prices, at which they can be redeemed after liquidation event
    uint256[] internal tpLiqPrices;
    // pct of the gain because Pegged Tokens devaluation that is transferred
    // in Collateral Asset to Moc Fee Flow during the settlement [PREC]
    uint256 public successFee;
    // pct of the gain because Pegged Tokens devaluation that is returned
    // in Pegged Tokens to appreciation beneficiary during the settlement [PREC]
    uint256 public appreciationFactor;

    // ------- Storage Fees -------

    // pct retain on fees to be re-injected as Collateral, while paying fees with AC [PREC]
    uint256 public feeRetainer; // 0% = 0; 1% = 10 ** 16; 100% = 10 ** 18
    // addition fee pct applied on Collateral Tokens mint [PREC]
    uint256 public tcMintFee; // 0% = 0; 1% = 10 ** 16; 100% = 10 ** 18
    // addition fee pct applied on Collateral Tokens redeem [PREC]
    uint256 public tcRedeemFee; // 0% = 0; 1% = 10 ** 16; 100% = 10 ** 18
    // additional fee pct applied on swap a Pegged Token for another Pegged Token [PREC]
    uint256 public swapTPforTPFee; // 0% = 0; 1% = 10 ** 16; 100% = 10 ** 18
    // additional fee pct applied on swap a Pegged Token for Collateral Token [PREC]
    uint256 public swapTPforTCFee; // 0% = 0; 1% = 10 ** 16; 100% = 10 ** 18
    // additional fee pct applied on swap Collateral Token for a Pegged Token [PREC]
    uint256 public swapTCforTPFee; // 0% = 0; 1% = 10 ** 16; 100% = 10 ** 18
    // additional fee pct applied on redeem Collateral Token and Pegged Token in one operations [PREC]
    uint256 public redeemTCandTPFee; // 0% = 0; 1% = 10 ** 16; 100% = 10 ** 18
    // additional fee pct applied on mint Collateral Token and Pegged Token in one operation [PREC]
    uint256 public mintTCandTPFee; // 0% = 0; 1% = 10 ** 16; 100% = 10 ** 18

    // addition fee pct applied on Pegged Tokens mint [PREC]
    uint256[] public tpMintFee; // 0% = 0; 1% = 10 ** 16; 100% = 10 ** 18
    // addition fee pct applied on Pegged Tokens redeem [PREC]
    uint256[] public tpRedeemFee; // 0% = 0; 1% = 10 ** 16; 100% = 10 ** 18

    // Moc Fee Flow contract address
    address public mocFeeFlowAddress;
    // Moc appreciation beneficiary address
    address public mocAppreciationBeneficiaryAddress;

    // ------- Storage Coverage Tracking -------

    // Target coverage for each Pegged Token [PREC]
    uint256[] public tpCtarg;
    // Coverage protected state threshold [PREC]
    uint256 public protThrld;
    // Coverage liquidation threshold [PREC]
    uint256 public liqThrld;
    // Liquidation enabled
    bool public liqEnabled;
    // Irreversible state, peg lost, contract is terminated and all funds can be withdrawn
    bool public liquidated;

    // ------- Storage Success Fee Tracking -------

    // profit and loss in collateral asset for each Pegged Token because its devaluation [N]
    // if it is positive it is a profit that will be distributed and reset during settlement
    int256[] internal tpiou;
    // Pegged Token price used at last operation(redeem or mint) [PREC]
    uint256[] internal pACtpLstop;

    // ------- Modifiers -------
    /// @notice functions with this modifier reverts being in liquidated state
    modifier notLiquidated() {
        _checkLiquidated();
        _;
    }

    // ------- Initializer -------
    /**
     * @notice contract initializer
     * @param initializeBaseBucketParams_ contract initializer params
     * @dev   tcTokenAddress Collateral Token contract address
     *        mocFeeFlowAddress Moc Fee Flow contract address
     *        mocAppreciationBeneficiaryAddress Moc appreciation beneficiary address
     *        protThrld protected coverage threshold [PREC]
     *        liqThrld liquidation coverage threshold [PREC]
     *        feeRetainer pct retain on fees to be re-injected as Collateral, while paying fees with AC [PREC]
     *        tcMintFee additional fee pct applied on mint Collateral Tokens operations [PREC]
     *        tcRedeemFee additional fee pct applied on redeem Collateral Tokens operations [PREC]
     *        swapTPforTPFee additional fee pct applied on swap a Pegged Token for another Pegged Token [PREC]
     *        swapTPforTCFee additional fee pct applied on swap a Pegged Token for Collateral Token [PREC]
     *        swapTCforTPFee additional fee pct applied on swap Collateral Token for a Pegged Token [PREC]
     *        redeemTCandTPFee additional fee pct applied on redeem Collateral Token and Pegged Token [PREC]
     *        mintTCandTPFee additional fee pct applied on mint Collateral Token and Pegged Token [PREC]
     *        successFee pct of the gain because Pegged Tokens devaluation that is transferred
     *          in Collateral Asset to Moc Fee Flow during the settlement [PREC]
     *        appreciationFactor pct of the gain because Pegged Tokens devaluation that is returned
     *          in Pegged Tokens to appreciation beneficiary during the settlement [PREC]
     */
    function __MocBaseBucket_init_unchained(
        InitializeBaseBucketParams calldata initializeBaseBucketParams_
    ) internal onlyInitializing {
        if (initializeBaseBucketParams_.protThrld < ONE) revert InvalidValue();
        _checkLessThanOne(initializeBaseBucketParams_.feeRetainer);
        _checkLessThanOne(initializeBaseBucketParams_.tcMintFee);
        _checkLessThanOne(initializeBaseBucketParams_.tcRedeemFee);
        _checkLessThanOne(initializeBaseBucketParams_.swapTPforTPFee);
        _checkLessThanOne(initializeBaseBucketParams_.swapTPforTCFee);
        _checkLessThanOne(initializeBaseBucketParams_.swapTCforTPFee);
        _checkLessThanOne(initializeBaseBucketParams_.redeemTCandTPFee);
        _checkLessThanOne(initializeBaseBucketParams_.mintTCandTPFee);
        _checkLessThanOne(initializeBaseBucketParams_.successFee + initializeBaseBucketParams_.appreciationFactor);
        tcToken = MocTC(initializeBaseBucketParams_.tcTokenAddress);
        // Verifies it has the right roles over this TC
        if (!tcToken.hasFullRoles(address(this))) revert InvalidAddress();
        mocFeeFlowAddress = initializeBaseBucketParams_.mocFeeFlowAddress;
        mocAppreciationBeneficiaryAddress = initializeBaseBucketParams_.mocAppreciationBeneficiaryAddress;
        protThrld = initializeBaseBucketParams_.protThrld;
        liqThrld = initializeBaseBucketParams_.liqThrld;
        feeRetainer = initializeBaseBucketParams_.feeRetainer;
        tcMintFee = initializeBaseBucketParams_.tcMintFee;
        tcRedeemFee = initializeBaseBucketParams_.tcRedeemFee;
        swapTPforTPFee = initializeBaseBucketParams_.swapTPforTPFee;
        swapTPforTCFee = initializeBaseBucketParams_.swapTPforTCFee;
        swapTCforTPFee = initializeBaseBucketParams_.swapTCforTPFee;
        redeemTCandTPFee = initializeBaseBucketParams_.redeemTCandTPFee;
        mintTCandTPFee = initializeBaseBucketParams_.mintTCandTPFee;
        successFee = initializeBaseBucketParams_.successFee;
        appreciationFactor = initializeBaseBucketParams_.appreciationFactor;
        liquidated = false;
        liqEnabled = false;
    }

    // ------- Internal Functions -------

    /**
     * reverts if in liquidated state
     */
    function _checkLiquidated() internal view {
        if (liquidated) revert Liquidated();
    }

    /**
     * @notice Adds Collateral Asset to the Bucket
     * @param qAC_ amount of Collateral Asset to add
     */
    function _depositAC(uint256 qAC_) internal {
        nACcb += qAC_;
    }

    /**
     * @notice Adds Collateral Token and Collateral Asset to the Bucket
     * @param qTC_ amount of Collateral Token to add
     * @param qAC_ amount of Collateral Asset to add
     */
    function _depositTC(uint256 qTC_, uint256 qAC_) internal {
        nTCcb += qTC_;
        _depositAC(qAC_);
    }

    /**
     * @notice Subtracts Collateral Token and Collateral Asset from the Bucket
     * @param qTC_ amount of Collateral Token to subtract
     * @param qAC_ amount of Collateral Asset to subtract
     */
    function _withdrawTC(uint256 qTC_, uint256 qAC_) internal {
        nTCcb -= qTC_;
        nACcb -= qAC_;
    }

    /**
     * @notice Adds Pegged Token and Collateral Asset to the Bucket
     * @param i_ Pegged Token index
     * @param qTP_ amount of Pegged Token to add
     * @param qAC_ amount of Collateral Asset to add
     */
    function _depositTP(uint256 i_, uint256 qTP_, uint256 qAC_) internal {
        pegContainer[i_].nTP += qTP_;
        _depositAC(qAC_);
    }

    /**
     * @notice Subtracts Pegged Token and Collateral Asset from the Bucket
     * @param i_ Pegged Token index
     * @param qTP_ amount of Pegged Token to subtract
     * @param qAC_ amount of Collateral Asset to subtract
     */
    function _withdrawTP(uint256 i_, uint256 qTP_, uint256 qAC_) internal {
        pegContainer[i_].nTP -= qTP_;
        nACcb -= qAC_;
    }

    /**
     * @notice Adds Pegged Token and Collateral Asset to the Bucket and mints `qTP_` for Pegged Token `i_`
     * @param i_ Pegged Token index
     * @param qTP_ amount of Pegged Token to add
     * @param qAC_ amount of Collateral Asset to add
     * @param recipient_ the account to mint tokens to
     */
    function _depositAndMintTP(uint256 i_, uint256 qTP_, uint256 qAC_, address recipient_) internal {
        // add qTP and qAC to the Bucket
        _depositTP(i_, qTP_, qAC_);
        // mint qTP to the recipient
        tpTokens[i_].mint(recipient_, qTP_);
    }

    /**
     * @notice subtracts Pegged Token and Collateral Asset from the Bucket and burns `qTP_` for Pegged Token `i_`
     * @param i_ Pegged Token index
     * @param qTP_ amount of Pegged Token to subtract
     * @param qAC_ amount of Collateral Asset to subtract
     * @param toBurnFrom_ the account to burn tokens from
     */
    function _withdrawAndBurnTP(uint256 i_, uint256 qTP_, uint256 qAC_, address toBurnFrom_) internal {
        // sub qTP and qAC from the Bucket
        _withdrawTP(i_, qTP_, qAC_);
        // burn qTP from this address
        tpTokens[i_].burn(toBurnFrom_, qTP_);
    }

    /**
     * @notice Adds Collateral Token and Collateral Asset to the Bucket and mints qTCtoMint
     * @param qTC_ amount of Collateral Token to add
     * @param qAC_ amount of Collateral Asset to add
     * @param recipient_ the account to mint tokens to
     */
    function _depositAndMintTC(uint256 qTC_, uint256 qAC_, address recipient_) internal {
        // add qTC to the Bucket
        _depositTC(qTC_, qAC_);
        // mint qTC to the recipient
        tcToken.mint(recipient_, qTC_);
    }

    /**
     * @notice Subtracts Collateral Token and Collateral Asset from the Bucket and burns `qTC_`
     * @param qTC_ amount of Collateral Token to subtract
     * @param qAC_ amount of Collateral Asset to subtract
     * @param toBurnFrom_ the account to burn tokens from
     */
    function _withdrawAndBurnTC(uint256 qTC_, uint256 qAC_, address toBurnFrom_) internal {
        // sub qTC and qAC from the Bucket
        _withdrawTC(qTC_, qAC_);
        // burn qTC from this address
        tcToken.burn(toBurnFrom_, qTC_);
    }

    /**
     * @notice get amount of Collateral Asset available considering how many are locked by Pegged Token adjusted by EMA
     * @param ctargemaCA_ target coverage adjusted by the moving average of the value of the Collateral Asset [PREC]
     * @param lckAC_ amount of Collateral Asset locked by Pegged Token [N]
     * @param nACgain_ amount of collateral asset to be distributed during settlement [N]
     * @return lckACemaAdjusted [PREC]
     */
    function _getLckACemaAdjusted(
        uint256 ctargemaCA_,
        uint256 lckAC_,
        uint256 nACgain_
    ) internal view returns (uint256 lckACemaAdjusted) {
        // if coverage <= ctargemaCA, we force that there be 0 AC available due to possible rounding errors
        if (_getCglb(lckAC_, nACgain_) <= ctargemaCA_) return 0;
        // [PREC] = [N] * [PREC] - [PREC] * [N]
        return _getTotalACavailable(nACgain_) * PRECISION - ctargemaCA_ * lckAC_;
    }

    /**
     * @notice get amount of Collateral Token available to redeem
     * @param ctargemaCA_ target coverage adjusted by the moving average of the value of the Collateral Asset [PREC]
     * @param lckAC_ amount of Collateral Asset locked by Pegged Token [N]
     * @param nACgain_ amount of collateral asset to be distributed during settlement [N]
     * @return tcAvailableToRedeem [N]
     */
    function _getTCAvailableToRedeem(
        uint256 ctargemaCA_,
        uint256 lckAC_,
        uint256 nACgain_
    ) internal view returns (uint256 tcAvailableToRedeem) {
        // [PREC]
        uint256 lckACemaAdjusted = _getLckACemaAdjusted(ctargemaCA_, lckAC_, nACgain_);
        // [N] = [PREC] / [PREC]
        return lckACemaAdjusted / _getPTCac(lckAC_, nACgain_);
    }

    /**
     * @notice get amount of Pegged Token available to mint
     * @param ctargemaCA_ target coverage adjusted by the moving average of the value of the Collateral Asset
     * @param ctargemaTP_ target coverage adjusted by the moving average of the value of a Pegged Token
     * @param pACtp_ Collateral Asset price in amount of Pegged Token [PREC]
     * @param lckAC_ amount of Collateral Asset locked by Pegged Token [N]
     * @param nACgain_ amount of collateral asset to be distributed during settlement [N]
     * @return tpAvailableToMint [N]
     */
    function _getTPAvailableToMint(
        uint256 ctargemaCA_,
        uint256 ctargemaTP_,
        uint256 pACtp_,
        uint256 lckAC_,
        uint256 nACgain_
    ) internal view returns (uint256 tpAvailableToMint) {
        // [N] = ([PREC] * [PREC] / [PREC]) / [PREC]
        return ((_getLckACemaAdjusted(ctargemaCA_, lckAC_, nACgain_) * pACtp_) / (ctargemaTP_ - ONE)) / PRECISION;
    }

    /**
     * @notice evaluates whether or not the coverage is over the cThrld_, reverts if below
     * @param cThrld_ coverage threshold to check for [PREC]
     * @return lckAC amount of Collateral Asset locked by Pegged Tokens [PREC]
     * @return nACgain amount of collateral asset to be distributed during settlement [N]
     */
    function _evalCoverage(uint256 cThrld_) internal view returns (uint256 lckAC, uint256 nACgain) {
        (lckAC, nACgain) = _getLckACandACgain();
        uint256 cglb = _getCglb(lckAC, nACgain);
        // check if coverage is above the given threshold
        if (cglb <= cThrld_) revert LowCoverage(cglb, cThrld_);
    }

    /**
     * @dev Calculates price at liquidation event as a relation between Pegs total supply
     * and the amount of Asset Collateral available to distribute
     */
    function settleLiquidationPrices() internal {
        // Total amount of AC available to be redeemed
        uint256 totalACAvailable = nACcb + nACioucb;
        if (totalACAvailable == 0) return;
        uint256 pegAmount = pegContainer.length;
        // this could be get by getLckAC(), but given the prices are needed after,
        // it's better to cache them here.
        uint256 lckAC;
        // Auxiliary cache of pegs pACtp
        uint256[] memory pACtps = new uint256[](pegAmount);
        // for each peg, calculates the proportion of AC reserves it's locked

        for (uint256 i = 0; i < pegAmount; i = unchecked_inc(i)) {
            pACtps[i] = getPACtp(i);
            // [N] = [N] * [PREC] / [PREC]
            lckAC += _divPrec(pegContainer[i].nTP, pACtps[i]);
        }
        for (uint256 i = 0; i < pegAmount; i = unchecked_inc(i)) {
            // [PREC] = [PREC] * [N] / [N];
            tpLiqPrices.push((pACtps[i] * lckAC) / totalACAvailable);
        }
    }

    /**
     * @notice updates Pegged Token P&L and last operation price
     * @param i_ Pegged Token index
     * @param pACtp_ Pegged Token price [PREC]
     */
    function _updateTPtracking(uint256 i_, uint256 pACtp_) internal {
        tpiou[i_] += _calcOtfPnLTP(i_, pACtp_);
        pACtpLstop[i_] = pACtp_;
    }

    /**
     * @notice calculates on the fly Pegged Token P&L
     * @param i_ Pegged Token index
     * @param pACtp_ Pegged Token price [PREC]
     * @return otfPnLtp [N]
     */
    function _calcOtfPnLTP(uint256 i_, uint256 pACtp_) internal view returns (int256 otfPnLtp) {
        // [PREC] = [N] * [PREC]
        uint256 nTP = pegContainer[i_].nTP * PRECISION;
        // [N] = [PREC] / [PREC] - [PREC] / [PREC]
        return int256(nTP / pACtpLstop[i_]) - int256(nTP / pACtp_);
    }

    /**
     * @notice gets accumulated Pegged Token P&L
     * @param i_ Pegged Token index
     * @param pACtp_ Pegged Token price [PREC]
     * @return tpGain amount of Pegged Token to be minted during settlement [N]
     * @return adjPnLtpi total amount of P&L in Collateral Asset [N]
     */
    function _getPnLTP(uint256 i_, uint256 pACtp_) internal view returns (uint256 tpGain, uint256 adjPnLtpi) {
        // [N] = [N] + [N]
        int256 adjPnLtpiAux = tpiou[i_] + _calcOtfPnLTP(i_, pACtp_);
        if (adjPnLtpiAux > 0) {
            adjPnLtpi = uint256(adjPnLtpiAux);
            // [N] = (([PREC] * [PREC] / [PREC]) * [N]) / [PREC]
            tpGain = _mulPrec(_mulPrec(appreciationFactor, pACtp_), adjPnLtpi);
        }
        return (tpGain, adjPnLtpi);
    }

    /**
     * @notice get amount of Collateral Asset locked by Pegged Token and
     *  amount of collateral asset to be distributed during settlement
     * @return lckAC [N]
     * @return nACgain [N]
     */
    function _getLckACandACgain() internal view returns (uint256 lckAC, uint256 nACgain) {
        uint256 pegAmount = pegContainer.length;
        for (uint256 i = 0; i < pegAmount; i = unchecked_inc(i)) {
            uint256 pACtp = getPACtp(i);
            (uint256 tpGain, uint256 adjPnLtpi) = _getPnLTP(i, pACtp);
            // [N] = ([N] + [N]) * [PREC] / [PREC]
            lckAC += _divPrec(pegContainer[i].nTP + tpGain, pACtp);
            nACgain += adjPnLtpi;
        }
        // [N] = [N] * [PREC] / [PREC]
        nACgain = _mulPrec(nACgain, successFee);
        return (lckAC, nACgain);
    }

    /**
     * @notice get total Collateral Asset available
     * @param nACgain_ amount of collateral asset to be distributed during settlement [N]
     * @return totalACavailable [N]
     */
    function _getTotalACavailable(uint256 nACgain_) internal view returns (uint256 totalACavailable) {
        // [N] = [N] + [N] - [N]
        return nACcb + nACioucb - nACgain_;
    }

    /**
     * @notice get Collateral Token price
     * @param lckAC_ amount of Collateral Asset locked by Pegged Token [N]
     * @param nACgain_ amount of collateral asset to be distributed during settlement [N]
     * @return pTCac [PREC]
     */
    function _getPTCac(uint256 lckAC_, uint256 nACgain_) internal view returns (uint256 pTCac) {
        if (nTCcb == 0) return ONE;
        // [PREC] = ([N] - [N]) * [PREC]) / [N]
        return _divPrec((_getTotalACavailable(nACgain_) - lckAC_), nTCcb);
    }

    /**
     * @notice get Collateral Token leverage
     * @param lckAC_ amount of Collateral Asset locked by Pegged Token [N]
     * @param nACgain_ amount of collateral asset to be distributed during settlement [N]
     * @return leverageTC [PREC]
     */
    function _getLeverageTC(uint256 lckAC_, uint256 nACgain_) internal view returns (uint256 leverageTC) {
        uint256 totalACavailable = _getTotalACavailable(nACgain_);
        // [PREC] = [N] * [PREC] / ([N] - [N])
        return _divPrec(totalACavailable, totalACavailable - lckAC_);
    }

    /**
     * @notice get bucket global coverage
     * @param lckAC_ amount of Collateral Asset locked by Pegged Token [N]
     * @param nACgain_ amount of collateral asset to be distributed during settlement [N]
     * @return cglob [PREC]
     */
    function _getCglb(uint256 lckAC_, uint256 nACgain_) internal view returns (uint256 cglob) {
        if (lckAC_ == 0) return UINT256_MAX;
        // [PREC] = [N] * [PREC] / [N]
        return _divPrec(_getTotalACavailable(nACgain_), lckAC_);
    }

    // ------- Public Functions -------

    /**
     * @notice If liquidation is enabled, verifies if forced liquidation has been
     * reached, checking if globalCoverage <= liquidation
     * @return true if liquidation state is reached, false otherwise
     */
    function isLiquidationReached() public view returns (bool) {
        (uint256 lckAC, uint256 nACgain) = _getLckACandACgain();
        uint256 cglb = _getCglb(lckAC, nACgain);
        return cglb <= liqThrld;
    }

    /**
     * @notice evaluates if liquidation threshold has been reached and liq is Enabled.
     * If so forces contracts liquidation, blocking all mint & redeem operations.
     *
     * May emit a {ContractLiquidated} event.
     */
    function evalLiquidation() external notPaused {
        if (liqEnabled && !liquidated && isLiquidationReached()) {
            liquidated = true;
            emit ContractLiquidated();
            tcToken.pause();
            // Freeze current Peg Price given the AC available
            settleLiquidationPrices();
        }
    }

    /**
     * @notice get how many Pegged Token equal 1 Collateral Asset
     * @param i_ Pegged Token index
     * @return price [PREC]
     */
    function getPACtp(uint256 i_) public view virtual returns (uint256) {
        IPriceProvider priceProvider = pegContainer[i_].priceProvider;
        (bytes32 price, bool has) = priceProvider.peek();
        if (!has) revert InvalidPriceProvider(address(priceProvider));
        return uint256(price);
    }

    // ------- Only Authorized Changer Functions -------

    /**
     * @dev sets the fee pct to be retainer on AC fees payments as AC re-injection.
     * @param feeRetainer_  pct retain on fees to be re-injected as Collateral, while paying fees with AC [PREC]
     * 0% = 0; 1% = 10 ** 16; 100% = 10 ** 18
     */
    function setFeeRetainer(uint256 feeRetainer_) external onlyAuthorizedChanger {
        feeRetainer = feeRetainer_;
    }

    /**
     * @dev sets the fee charged on Token Collateral mint.
     * @param tcMintFee_ addition fee pct applied on Collateral Tokens mint [PREC]
     * 0% = 0; 1% = 10 ** 16; 100% = 10 ** 18
     */
    function setTcMintFee(uint256 tcMintFee_) external onlyAuthorizedChanger {
        tcMintFee = tcMintFee_;
    }

    /**
     * @dev sets the fee charged on Token Collateral redeem.
     * @param tcRedeemFee_ addition fee pct applied on Collateral Tokens redeem [PREC]
     * 0% = 0; 1% = 10 ** 16; 100% = 10 ** 18
     */
    function setTcRedeemFee(uint256 tcRedeemFee_) external onlyAuthorizedChanger {
        tcRedeemFee = tcRedeemFee_;
    }

    /**
     * @dev sets the fee charged when swap a Pegged Token for another Pegged Token.
     * @param swapTPforTPFee_ additional fee pct applied on swap a Pegged Token for another Pegged Token [PREC]
     * 0% = 0; 1% = 10 ** 16; 100% = 10 ** 18
     */
    function setSwapTPforTPFee(uint256 swapTPforTPFee_) external onlyAuthorizedChanger {
        swapTPforTPFee = swapTPforTPFee_;
    }

    /**
     * @dev sets the fee charged when swap a Pegged Token for Collateral Token.
     * @param swapTPforTCFee_ additional fee pct applied on swap a Pegged Token for Collateral Token [PREC]
     * 0% = 0; 1% = 10 ** 16; 100% = 10 ** 18
     */
    function setSwapTPforTCFee(uint256 swapTPforTCFee_) external onlyAuthorizedChanger {
        swapTPforTCFee = swapTPforTCFee_;
    }

    /**
     * @dev sets the fee charged when swap Collateral Token for a Pegged Token.
     * @param swapTCforTPFee_ additional fee pct applied on swap Collateral Token for a Pegged Token [PREC]
     * 0% = 0; 1% = 10 ** 16; 100% = 10 ** 18
     */
    function setSwapTCforTPFee(uint256 swapTCforTPFee_) external onlyAuthorizedChanger {
        swapTCforTPFee = swapTCforTPFee_;
    }

    /**
     * @dev sets the fee charged when redeem Collateral Token and Pegged Token in one operation.
     * @param redeemTCandTPFee_ additional fee pct applied on redeem Collateral Token and Pegged Token [PREC]
     * 0% = 0; 1% = 10 ** 16; 100% = 10 ** 18
     */
    function setRedeemTCandTPFee(uint256 redeemTCandTPFee_) external onlyAuthorizedChanger {
        redeemTCandTPFee = redeemTCandTPFee_;
    }

    /**
     * @dev sets the fee charged when mint Collateral Token and Pegged Token in one operation.
     * @param mintTCandTPFee_ additional fee pct applied on mint Collateral Token and Pegged Token [PREC]
     * 0% = 0; 1% = 10 ** 16; 100% = 10 ** 18
     */
    function setMintTCandTPFee(uint256 mintTCandTPFee_) external onlyAuthorizedChanger {
        mintTCandTPFee = mintTCandTPFee_;
    }

    /**
     * @dev sets Moc Fee Flow contract address
     * @param mocFeeFlowAddress_ moc Fee Flow new contract address
     */
    function setMocFeeFlowAddress(address mocFeeFlowAddress_) external onlyAuthorizedChanger {
        mocFeeFlowAddress = mocFeeFlowAddress_;
    }

    /**
     * @dev sets Moc Appreciation Beneficiary Address
     * @param mocAppreciationBeneficiaryAddress_ moc Appreciation Beneficiary new address
     */
    function setMocAppreciationBeneficiaryAddress(
        address mocAppreciationBeneficiaryAddress_
    ) external onlyAuthorizedChanger {
        mocAppreciationBeneficiaryAddress = mocAppreciationBeneficiaryAddress_;
    }

    /**
     * @dev sets the value of the protected threshold configuration param
     * @param protThrld_ coverage protected state threshold [PREC]
     */
    function setProtThrld(uint256 protThrld_) external onlyAuthorizedChanger {
        protThrld = protThrld_;
    }

    /**
     * @dev sets the value of the liq threshold configuration param
     * @param liqThrld_ liquidation threshold
     */
    function setLiqThrld(uint256 liqThrld_) external onlyAuthorizedChanger {
        liqThrld = liqThrld_;
    }

    /**
     * @dev enables and disables the liquidation mechanism.
     * @param liqEnabled_ is liquidation enabled
     */
    function setLiqEnabled(bool liqEnabled_) external onlyAuthorizedChanger {
        liqEnabled = liqEnabled_;
    }

    /**
     * @dev sets success Fee value.
     * @param successFee_ pct of the gain because Pegged Tokens devaluation that is
     * transferred in Collateral Asset to Moc Fee Flow during the settlement [PREC]
     */
    function setSuccessFee(uint256 successFee_) external onlyAuthorizedChanger {
        successFee = successFee_;
    }

    /**
     * @dev sets appreciation Factor value.
     * @param appreciationFactor_ pct of the gain because Pegged Tokens devaluation that is returned
     * in Pegged Tokens to appreciation beneficiary during the settlement [PREC]
     */
    function setAppreciationFactor(uint256 appreciationFactor_) external onlyAuthorizedChanger {
        appreciationFactor = appreciationFactor_;
    }

    /**
     * @dev This empty reserved space is put in place to allow future versions to add new
     * variables without shifting down storage in the inheritance chain.
     * See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
     */
    uint256[50] private __gap;
}
","keccak256":"0x7dac77ba4b507e70d9bb09f72305e6adacf60ce1ddb127aeb6c83d5c96313289"},"contracts/core/MocCore.sol":{"content":"pragma solidity ^0.8.17;

import "../interfaces/IMocRC20.sol";
import "./MocSettlement.sol";

/**
 * @title MocCore
 * @notice MocCore nucleates all the basic MoC functionality and tool set. It allows Collateral
 * asset aware contracts to implement the main mint/redeem operations.
 */
abstract contract MocCore is MocSettlement {
    // ------- Events -------
    event TCMinted(address indexed sender_, address indexed recipient_, uint256 qTC_, uint256 qAC_, uint256 qACfee_);
    event TCRedeemed(address indexed sender_, address indexed recipient_, uint256 qTC_, uint256 qAC_, uint256 qACfee_);
    event TPMinted(
        uint256 indexed i_,
        address indexed sender_,
        address indexed recipient_,
        uint256 qTP_,
        uint256 qAC_,
        uint256 qACfee_
    );
    event TPRedeemed(
        uint256 indexed i_,
        address indexed sender_,
        address indexed recipient_,
        uint256 qTP_,
        uint256 qAC_,
        uint256 qACfee_
    );
    event TPSwappedForTP(
        uint256 indexed iFrom_,
        uint256 iTo_,
        address indexed sender_,
        address indexed recipient_,
        uint256 qTPfrom_,
        uint256 qTPto_,
        uint256 qACfee_
    );
    event TPSwappedForTC(
        uint256 indexed i_,
        address indexed sender_,
        address indexed recipient_,
        uint256 qTP_,
        uint256 qTC_,
        uint256 qACfee_
    );
    event TCSwappedForTP(
        uint256 indexed i_,
        address indexed sender_,
        address indexed recipient_,
        uint256 qTC_,
        uint256 qTP_,
        uint256 qACfee_
    );
    event TCandTPRedeemed(
        uint256 indexed i_,
        address indexed sender_,
        address indexed recipient_,
        uint256 qTC_,
        uint256 qTP_,
        uint256 qAC_,
        uint256 qACfee_
    );
    event TCandTPMinted(
        uint256 indexed i_,
        address indexed sender_,
        address indexed recipient_,
        uint256 qTC_,
        uint256 qTP_,
        uint256 qAC_,
        uint256 qACfee_
    );
    event PeggedTokenChange(uint256 indexed i_, PeggedTokenParams peggedTokenParams_);
    event SuccessFeeDistributed(uint256 mocGain_, uint256[] tpGain_);
    // ------- Custom Errors -------
    error PeggedTokenAlreadyAdded();
    error InsufficientQacSent(uint256 qACsent_, uint256 qACNeeded_);
    error QacBelowMinimumRequired(uint256 qACmin_, uint256 qACtoRedeem_);
    error InsufficientTPtoMint(uint256 qTP_, uint256 tpAvailableToMint_);
    error InsufficientTCtoRedeem(uint256 qTC_, uint256 tcAvailableToRedeem_);
    error InsufficientTPtoRedeem(uint256 qTP_, uint256 tpAvailableToRedeem_);
    error QacNeededMustBeGreaterThanZero();
    error QtpBelowMinimumRequired(uint256 qTPmin_, uint256 qTP_);
    error QtcBelowMinimumRequired(uint256 qTCmin_, uint256 qTC_);
    error InsufficientQtpSent(uint256 qTPsent_, uint256 qTPNeeded_);
    // ------- Structs -------

    struct InitializeCoreParams {
        InitializeBaseBucketParams initializeBaseBucketParams;
        // The address that will define when a change contract is authorized
        address governorAddress;
        // The address that is authorized to pause this contract
        address pauserAddress;
        // amount of blocks to wait between Pegged ema calculation
        uint256 emaCalculationBlockSpan;
        // number of blocks between settlements
        uint256 bes;
    }

    struct PeggedTokenParams {
        // Pegged Token contract address to add
        address tpTokenAddress;
        // priceProviderAddress Pegged Token price provider contract address
        address priceProviderAddress;
        // Pegged Token target coverage [PREC]
        uint256 tpCtarg;
        // additional fee pct applied on mint [PREC]
        uint256 tpMintFee;
        // additional fee pct applied on redeem [PREC]
        uint256 tpRedeemFee;
        // initial Pegged Token exponential moving average [PREC]
        uint256 tpEma;
        // Pegged Token smoothing factor [PREC]
        uint256 tpEmaSf;
    }

    // ------- Initializer -------
    /**
     * @notice contract initializer
     * @dev this function must be execute by the AC implementation at initialization
     * @param initializeCoreParams_ contract initializer params
     *        governorAddress The address that will define when a change contract is authorized
     *        pauserAddress_ The address that is authorized to pause this contract
     *        tcTokenAddress Collateral Token contract address
     *        mocFeeFlowAddress Moc Fee Flow contract address
     *        mocAppreciationBeneficiaryAddress Moc appreciation beneficiary address
     *        protThrld protected state threshold [PREC]
     *        liqThrld liquidation coverage threshold [PREC]
     *        feeRetainer pct retain on fees to be re-injected as Collateral, while paying fees with AC [PREC]
     *        tcMintFee additional fee pct applied on mint Collateral Tokens operations [PREC]
     *        tcRedeemFee additional fee pct applied on redeem Collateral Tokens operations [PREC]
     *        successFee pct of the gain because Pegged Tokens devaluation that is transferred
     *          in Collateral Asset to Moc Fee Flow during the settlement [PREC]
     *        appreciationFactor pct of the gain because Pegged Tokens devaluation that is returned
     *          in Pegged Tokens to appreciation beneficiary during the settlement [PREC]]
     *        emaCalculationBlockSpan amount of blocks to wait between Pegged ema calculation
     *        bes number of blocks between settlements
     */
    function __MocCore_init(InitializeCoreParams calldata initializeCoreParams_) internal onlyInitializing {
        __MocUpgradable_init(initializeCoreParams_.governorAddress, initializeCoreParams_.pauserAddress);
        __MocBaseBucket_init_unchained(initializeCoreParams_.initializeBaseBucketParams);
        __MocEma_init_unchained(initializeCoreParams_.emaCalculationBlockSpan);
        __MocSettlement_init_unchained(initializeCoreParams_.bes);
    }

    // ------- Internal Functions -------

    /**
     * @notice transfer Collateral Asset
     * @dev this function must be overridden by the AC implementation
     *  and revert if transfer fails.
     * @param to_ address who receives the Collateral Asset
     * @param amount_ amount of Collateral Asset to transfer
     */
    function acTransfer(address to_, uint256 amount_) internal virtual;

    /**
     * @notice Collateral Asset balance
     * @dev this function must be overridden by the AC implementation
     * @param account address who's Collateral Asset balance we want to know of
     * @return balance `account`'s total amount of Collateral Asset
     */
    function acBalanceOf(address account) internal view virtual returns (uint256 balance);

    /**
     * @notice hook before any AC reception involving operation
     * @dev this function must be overridden by the AC implementation
     * @param qACMax_ max amount of AC available
     * @param qACNeeded_ amount of AC needed
     * @return change amount needed to be return to the sender after the operation is complete
     */
    function _onACNeededOperation(uint256 qACMax_, uint256 qACNeeded_) internal virtual returns (uint256 change);

    /**
     * @notice mint Collateral Token in exchange for Collateral Asset
     * @param qTC_ amount of Collateral Token to mint
     * @param qACmax_ maximum amount of Collateral Asset that can be spent
     * @param sender_ address who sends the Collateral Asset, all unspent amount is returned to it
     * @param recipient_ address who receives the Collateral Token
     * @return qACtotalNeeded amount of AC used to mint qTC
     */
    function _mintTCto(
        uint256 qTC_,
        uint256 qACmax_,
        address sender_,
        address recipient_
    ) internal notLiquidated notPaused returns (uint256 qACtotalNeeded) {
        // evaluates whether or not the system coverage is healthy enough to mint TC, reverts if it's not
        (uint256 lckAC, uint256 nACgain) = _evalCoverage(protThrld);
        // calculates how many qAC are needed to mint TC and the qAC fee
        (uint256 qACNeededtoMint, uint256 qACfee) = _calcQACforMintTC(qTC_, lckAC, nACgain);
        qACtotalNeeded = qACNeededtoMint + qACfee;
        if (qACtotalNeeded > qACmax_) revert InsufficientQacSent(qACmax_, qACtotalNeeded);
        // if is 0 reverts because it is trying to redeem an amount below precision
        if (qACtotalNeeded == 0) revert QacNeededMustBeGreaterThanZero();
        emit TCMinted(sender_, recipient_, qTC_, qACtotalNeeded, qACfee);
        _depositAndMintTC(qTC_, qACNeededtoMint, recipient_);
        uint256 acChange = _onACNeededOperation(qACmax_, qACtotalNeeded);
        // transfers any AC change to the sender and distributes fees
        _distOpResults(sender_, acChange, qACfee);
        return qACtotalNeeded;
    }

    /**
     * @notice redeem Collateral Asset in exchange for Collateral Token
     * @param qTC_ amount of Collateral Token to redeem
     * @param qACmin_ minimum amount of Collateral Asset that `recipient_` expects to receive
     * @param sender_ address who sends the Collateral Token
     * @param recipient_ address who receives the Collateral Asset
     * @return qACtoRedeem amount of AC sent to `recipient_`
     */
    function _redeemTCto(
        uint256 qTC_,
        uint256 qACmin_,
        address sender_,
        address recipient_
    ) internal notLiquidated notPaused returns (uint256 qACtoRedeem) {
        uint256 ctargemaCA = calcCtargemaCA();
        // evaluates whether or not the system coverage is healthy enough to redeem TC
        // given the target coverage adjusted by the moving average, reverts if it's not
        (uint256 lckAC, uint256 nACgain) = _evalCoverage(ctargemaCA);
        // evaluates if there are enough Collateral Tokens available to redeem, reverts if there are not
        _evalTCAvailableToRedeem(qTC_, ctargemaCA, lckAC, nACgain);
        // calculate how many total qAC are redeemed and how many correspond for fee
        (uint256 qACtotalToRedeem, uint256 qACfee) = _calcQACforRedeemTC(qTC_, lckAC, nACgain);
        // if is 0 reverts because it is trying to redeem an amount below precision
        if (qACtotalToRedeem == 0) revert QacNeededMustBeGreaterThanZero();
        qACtoRedeem = qACtotalToRedeem - qACfee;
        if (qACtoRedeem < qACmin_) revert QacBelowMinimumRequired(qACmin_, qACtoRedeem);
        emit TCRedeemed(sender_, recipient_, qTC_, qACtoRedeem, qACfee);
        _withdrawAndBurnTC(qTC_, qACtotalToRedeem, sender_);
        // transfers qAC to the recipient and distributes fees
        _distOpResults(recipient_, qACtoRedeem, qACfee);
        return qACtoRedeem;
    }

    /**
     * @notice mint Pegged Token in exchange for Collateral Asset
     * @param i_ Pegged Token index
     * @param qTP_ amount of Pegged Token to mint
     * @param qACmax_ maximum amount of Collateral Asset that can be spent
     * @param sender_ address who sends the Collateral Asset, all unspent amount is returned to it
     * @param recipient_ address who receives the Pegged Token
     * @return qACtotalNeeded amount of AC used to mint qTP
     */
    function _mintTPto(
        uint256 i_,
        uint256 qTP_,
        uint256 qACmax_,
        address sender_,
        address recipient_
    ) internal notLiquidated notPaused returns (uint256 qACtotalNeeded) {
        uint256 pACtp = getPACtp(i_);
        _updateTPtracking(i_, pACtp);
        uint256 ctargemaCA = calcCtargemaCA();
        // evaluates whether or not the system coverage is healthy enough to mint TP
        // given the target coverage adjusted by the moving average, reverts if it's not
        (uint256 lckAC, uint256 nACgain) = _evalCoverage(ctargemaCA);
        // evaluates if there are enough TP available to mint, reverts if it's not
        _evalTPavailableToMint(i_, qTP_, pACtp, ctargemaCA, lckAC, nACgain);
        // calculate how many qAC are needed to mint TP and the qAC fee
        (uint256 qACNeededtoMint, uint256 qACfee) = _calcQACforMintTP(i_, qTP_, pACtp);
        qACtotalNeeded = qACNeededtoMint + qACfee;
        if (qACtotalNeeded > qACmax_) revert InsufficientQacSent(qACmax_, qACtotalNeeded);
        // if is 0 reverts because it is trying to mint an amount below precision
        if (qACtotalNeeded == 0) revert QacNeededMustBeGreaterThanZero();
        emit TPMinted(i_, sender_, recipient_, qTP_, qACtotalNeeded, qACfee);
        // update bucket and mint
        _depositAndMintTP(i_, qTP_, qACNeededtoMint, recipient_);
        uint256 acChange = _onACNeededOperation(qACmax_, qACtotalNeeded);
        // transfers any AC change to the sender and distributes fees
        _distOpResults(sender_, acChange, qACfee);
        return qACtotalNeeded;
    }

    /**
     * @notice redeem Collateral Asset in exchange for Pegged Token
     * @param i_ Pegged Token index
     * @param qTP_ amount of Pegged Token to redeem
     * @param qACmin_ minimum amount of Collateral Asset that `recipient_` expects to receive
     * @param sender_ address who sends the Pegged Token
     * @param recipient_ address who receives the Collateral Asset
     * @return qACtoRedeem amount of AC sent to `recipient_`
     */
    function _redeemTPto(
        uint256 i_,
        uint256 qTP_,
        uint256 qACmin_,
        address sender_,
        address recipient_
    ) internal notLiquidated notPaused returns (uint256 qACtoRedeem) {
        uint256 pACtp = getPACtp(i_);
        _updateTPtracking(i_, pACtp);
        // evaluates whether or not the system coverage is healthy enough to redeem TP, reverts if it's not
        _evalCoverage(protThrld);
        // calculate how many total qAC are redeemed and how many correspond for fee
        (uint256 qACtotalToRedeem, uint256 qACfee) = _calcQACforRedeemTP(i_, qTP_, pACtp);
        // if is 0 reverts because it is trying to redeem an amount below precision
        if (qACtotalToRedeem == 0) revert QacNeededMustBeGreaterThanZero();
        qACtoRedeem = qACtotalToRedeem - qACfee;
        if (qACtoRedeem < qACmin_) revert QacBelowMinimumRequired(qACmin_, qACtoRedeem);
        emit TPRedeemed(i_, sender_, recipient_, qTP_, qACtoRedeem, qACfee);
        _withdrawAndBurnTP(i_, qTP_, qACtotalToRedeem, sender_);
        // transfers qAC to the recipient and distributes fees
        _distOpResults(recipient_, qACtoRedeem, qACfee);
        return qACtoRedeem;
    }

    /**
     * @notice mint Collateral Token and Pegged Token in exchange for Collateral Asset
     *  This operation is done without checking coverage
     *  Collateral Token and Pegged Token are minted in equivalent proportions so that its price
     *  and global coverage are not modified.
     *  Reverts if qAC sent are insufficient.
     * @param i_ Pegged Token index
     * @param qTP_ amount of Pegged Token to mint
     * @param qACmax_ maximum amount of Collateral Asset that can be spent
     * @param sender_ address who sends Collateral Asset
     * @param recipient_ address who receives the Collateral Token and Pegged Token
     * @return qACtotalNeeded amount of AC used to mint Collateral Token and Pegged Token
     * @return qTCtoMint amount of Collateral Token minted
     */
    function _mintTCandTPto(
        uint256 i_,
        uint256 qTP_,
        uint256 qACmax_,
        address sender_,
        address recipient_
    ) internal notLiquidated notPaused returns (uint256 qACtotalNeeded, uint256 qTCtoMint) {
        uint256 qACNeededtoMint;
        uint256 qACfee;
        uint256 pACtp = getPACtp(i_);
        _updateTPtracking(i_, pACtp);
        // evaluates that the system is not below the liquidation threshold
        // one of the reasons is to prevent it from failing due to underflow because the lckAC > totalACavailable
        _evalCoverage(liqThrld);
        (qTCtoMint, qACNeededtoMint, qACfee) = _calcQACforMintTCandTP(qTP_, pACtp);
        qACtotalNeeded = qACNeededtoMint + qACfee;
        if (qACtotalNeeded > qACmax_) revert InsufficientQacSent(qACmax_, qACtotalNeeded);
        // if is 0 reverts because it is trying to mint an amount below precision
        if (qACtotalNeeded == 0) revert QacNeededMustBeGreaterThanZero();
        emit TCandTPMinted(i_, sender_, recipient_, qTCtoMint, qTP_, qACtotalNeeded, qACfee);
        _depositAndMintTC(qTCtoMint, qACNeededtoMint, recipient_);
        _depositAndMintTP(i_, qTP_, 0, recipient_);
        uint256 acChange = _onACNeededOperation(qACmax_, qACtotalNeeded);
        // transfers qAC to the sender and distributes fees
        _distOpResults(sender_, acChange, qACfee);
        return (qACtotalNeeded, qTCtoMint);
    }

    /**
     * @notice redeem Collateral Asset in exchange for Collateral Token and Pegged Token
     *  This operation is done without checking coverage
     *  Collateral Token and Pegged Token are redeemed in equivalent proportions so that its price
     *  and global coverage are not modified.
     *  Reverts if qTP sent are insufficient.
     * @param i_ Pegged Token index
     * @param qTC_ amount of Collateral Token to redeem
     * @param qTP_ maximum amount of Pegged Token to redeem
     * @param qACmin_ minimum amount of Collateral Asset that `recipient_` expects to receive
     * @param sender_ address who sends Collateral Token and Pegged Token
     * @param recipient_ address who receives the Collateral Asset
     * @return qACtoRedeem amount of AC sent to `recipient_`
     * @return qTPtoRedeem amount of Pegged Token redeemed
     */
    function _redeemTCandTPto(
        uint256 i_,
        uint256 qTC_,
        uint256 qTP_,
        uint256 qACmin_,
        address sender_,
        address recipient_
    ) internal notLiquidated notPaused returns (uint256 qACtoRedeem, uint256 qTPtoRedeem) {
        uint256 pACtp = getPACtp(i_);
        _updateTPtracking(i_, pACtp);
        // evaluates that the system is not below the liquidation threshold
        // one of the reasons is to prevent it from failing due to underflow because the lckAC > totalACavailable
        (uint256 lckAC, uint256 nACgain) = _evalCoverage(liqThrld);
        // calculate how many TP are needed to redeem TC and not change coverage
        // qTPtoRedeem = (qTC * pACtp * pTCac) / (cglb - 1)
        // pTCac = (totalACavailable - lckAC) / nTCcb
        // cglb = totalACavailable / lckAC => cglb - 1 = (totalACavailable - lckAC) / lckAC
        // qTPtoRedeem = (qTC * pACtp * (totalACavailable - lckAC) / nTCcb) / ((totalACavailable - lckAC) / lckAC)
        // So, we can simplify (totalACavailable - lckAC)
        // qTPtoRedeem = (qTC * pACtp * lckAC) / nTCcb
        // [N] = ([N] * [N] * [PREC] / [N]) /  [PREC]
        qTPtoRedeem = ((qTC_ * lckAC * pACtp) / nTCcb) / PRECISION;

        if (qTPtoRedeem > qTP_) revert InsufficientQtpSent(qTP_, qTPtoRedeem);
        (uint256 qACtotalToRedeem, uint256 qACfee) = _calcQACforRedeemTCandTP(
            i_,
            qTC_,
            qTPtoRedeem,
            pACtp,
            _getPTCac(lckAC, nACgain)
        );
        qACtoRedeem = qACtotalToRedeem - qACfee;
        if (qACtoRedeem < qACmin_) revert QacBelowMinimumRequired(qACmin_, qACtoRedeem);

        // inside a block to avoid stack too deep error
        {
            uint256 i = i_;
            uint256 qTC = qTC_;
            uint256 qACtoRedeem_ = qACtoRedeem;
            emit TCandTPRedeemed(i, sender_, recipient_, qTC, qTPtoRedeem, qACtoRedeem_, qACfee);
        }

        _withdrawAndBurnTC(qTC_, qACtotalToRedeem, sender_);
        _withdrawAndBurnTP(i_, qTPtoRedeem, 0, sender_);

        // transfers qAC to the recipient and distributes fees
        _distOpResults(recipient_, qACtoRedeem, qACfee);
        return (qACtoRedeem, qTPtoRedeem);
    }

    /**
     * @notice swap Pegged Token to another one
     *  This operation is done without checking coverage unless the target coverage for
     *  received Pegged Token is greater than the Pegged Token sent
     * @param iFrom_ owned Pegged Token index
     * @param iTo_ target Pegged Token index
     * @param qTP_ amount of owned Pegged Token to swap
     * @param qTPmin_ minimum amount of target Pegged Token that `recipient_` expects to receive
     * @param qACmax_ maximum amount of Collateral Asset that can be spent in fees
     * @param sender_ address who sends the Pegged Token
     * @param recipient_ address who receives the target Pegged Token
     * @return qACfee amount of AC used to pay fee
     * @return qTPtoMint amount of Pegged Token minted
     */
    function _swapTPforTPto(
        uint256 iFrom_,
        uint256 iTo_,
        uint256 qTP_,
        uint256 qTPmin_,
        uint256 qACmax_,
        address sender_,
        address recipient_
    ) internal notLiquidated notPaused returns (uint256 qACfee, uint256 qTPtoMint) {
        if (iFrom_ == iTo_) revert InvalidValue();
        uint256 pACtpFrom = getPACtp(iFrom_);
        uint256 pACtpTo = getPACtp(iTo_);
        _updateTPtracking(iFrom_, pACtpFrom);
        _updateTPtracking(iTo_, pACtpTo);
        // calculate how many total qAC are redeemed and how many correspond for fee
        (uint256 qACtotalToRedeem, ) = _calcQACforRedeemTP(iFrom_, qTP_, pACtpFrom);
        // calculate how many qTP can mint with the given qAC
        // [N] = [N] * [PREC] / [PREC]
        qTPtoMint = (qTP_ * pACtpTo) / pACtpFrom;
        if (qTPtoMint < qTPmin_ || qTPtoMint == 0) revert QtpBelowMinimumRequired(qTPmin_, qTPtoMint);

        // if ctargemaTPto > ctargemaTPfrom we need to check coverage
        if (_getCtargemaTP(iTo_, pACtpTo) > _getCtargemaTP(iFrom_, pACtpFrom)) {
            uint256 ctargemaCA = calcCtargemaCA();
            // evaluates whether or not the system coverage is healthy enough to mint TP
            // given the target coverage adjusted by the moving average, reverts if it's not
            (uint256 lckAC, uint256 nACgain) = _evalCoverage(ctargemaCA);
            // evaluates if there are enough TP available to mint, reverts if it's not
            _evalTPavailableToMint(iTo_, qTPtoMint, pACtpTo, ctargemaCA, lckAC, nACgain);
        }

        // calculates qAC to be charged as fee
        // [N] = [N] * [PREC] / [PREC]
        qACfee = _mulPrec(qACtotalToRedeem, swapTPforTPFee);
        if (qACfee > qACmax_) revert InsufficientQacSent(qACmax_, qACfee);

        // inside a block to avoid stack too deep error
        {
            uint256 iFrom = iFrom_;
            uint256 iTo = iTo_;
            uint256 qTP = qTP_;
            emit TPSwappedForTP(iFrom, iTo, sender_, recipient_, qTP, qTPtoMint, qACfee);
        }

        _depositAndMintTP(iTo_, qTPtoMint, 0, recipient_);
        _withdrawAndBurnTP(iFrom_, qTP_, 0, sender_);

        // AC is only used to pay fees
        uint256 acChange = _onACNeededOperation(qACmax_, qACfee);
        // transfer any qAC change to the sender and distribute fees
        _distOpResults(sender_, acChange, qACfee);
        return (qACfee, qTPtoMint);
    }

    /**
     * @notice swap Pegged Token to Collateral Token
     * @param i_ Pegged Token index
     * @param qTP_ amount Pegged Token to swap
     * @param qTCmin_ minimum amount of Collateral Token that `recipient_` expects to receive
     * @param qACmax_ maximum amount of Collateral Asset that can be spent in fees
     * @param sender_ address who sends the Pegged Token
     * @param recipient_ address who receives Collateral Token
     * @return qACfee amount of AC used to pay fee
     * @return qTCtoMint amount of Collateral Token minted
     */
    function _swapTPforTCto(
        uint256 i_,
        uint256 qTP_,
        uint256 qTCmin_,
        uint256 qACmax_,
        address sender_,
        address recipient_
    ) internal notLiquidated notPaused returns (uint256 qACfee, uint256 qTCtoMint) {
        uint256 pACtp = getPACtp(i_);
        _updateTPtracking(i_, pACtp);
        // evaluates whether or not the system coverage is healthy enough to mint TC, reverts if it's not
        (uint256 lckAC, uint256 nACgain) = _evalCoverage(protThrld);
        // calculate how many total qAC are redeemed and how many correspond for fee
        (uint256 qACtotalToRedeem, ) = _calcQACforRedeemTP(i_, qTP_, pACtp);
        // calculate how many qTC can mint with the given qAC
        // qTCtoMint = qTP / pTCac / pACtp
        // [N] = [N] * [N] * [PREC] / ([N] - [N]) * [PREC]
        qTCtoMint = _divPrec(qTP_ * nTCcb, (_getTotalACavailable(nACgain) - lckAC) * pACtp);
        if (qTCtoMint < qTCmin_ || qTCtoMint == 0) revert QtcBelowMinimumRequired(qTCmin_, qTCtoMint);

        // calculate qAC to be charged as fee
        // [N] = [N] * [PREC] / [PREC]
        qACfee = _mulPrec(qACtotalToRedeem, swapTPforTCFee);
        if (qACfee > qACmax_) revert InsufficientQacSent(qACmax_, qACfee);

        // inside a block to avoid stack too deep error
        {
            uint256 i = i_;
            uint256 qTP = qTP_;
            emit TPSwappedForTC(i, sender_, recipient_, qTP, qTCtoMint, qACfee);
        }

        _withdrawAndBurnTP(i_, qTP_, 0, sender_);
        _depositAndMintTC(qTCtoMint, 0, recipient_);
        // AC is only used to pay fees
        uint256 acChange = _onACNeededOperation(qACmax_, qACfee);
        // transfer any qAC change to the sender and distribute fees
        _distOpResults(sender_, acChange, qACfee);
        return (qACfee, qTCtoMint);
    }

    /**
     * @notice swap Collateral Token to Pegged Token
     * @param i_ Pegged Token index
     * @param qTC_ amount of Collateral Token to swap
     * @param qTPmin_ minimum amount of Pegged Token Token that `recipient_` expects to receive
     * @param qACmax_ maximum amount of Collateral Asset that can be spent in fees
     * @param sender_ address who sends the Collateral Token
     * @param recipient_ address who receives the Pegged Token
     * @return qACfee amount of AC used to pay fee
     * @return qTPtoMint amount of Pegged Token minted
     */
    function _swapTCforTPto(
        uint256 i_,
        uint256 qTC_,
        uint256 qTPmin_,
        uint256 qACmax_,
        address sender_,
        address recipient_
    ) internal notLiquidated notPaused returns (uint256 qACfee, uint256 qTPtoMint) {
        uint256 pACtp = getPACtp(i_);
        _updateTPtracking(i_, pACtp);
        uint256 ctargemaCA = calcCtargemaCA();
        // evaluates whether or not the system coverage is healthy enough to redeem TC
        // given the target coverage adjusted by the moving average, reverts if it's not
        (uint256 lckAC, uint256 nACgain) = _evalCoverage(ctargemaCA);
        // evaluates if there are enough Collateral Tokens available to redeem, reverts if there are not
        _evalTCAvailableToRedeem(qTC_, ctargemaCA, lckAC, nACgain);
        // calculate how many total qAC are redeemed and how many correspond for fee
        (uint256 qACtotalToRedeem, ) = _calcQACforRedeemTC(qTC_, lckAC, nACgain);
        // if is 0 reverts because it is trying to swap an amount below precision
        if (qACtotalToRedeem == 0) revert QacNeededMustBeGreaterThanZero();
        // calculate how many qTP can mint with the given qAC
        // qTPtoMint = qTC * pTCac * pACtp
        // [N] = ([N] * ([N] - [N]) * [PREC] / [N]) / [PREC]
        qTPtoMint = ((qTC_ * (_getTotalACavailable(nACgain) - lckAC) * pACtp) / nTCcb) / PRECISION;
        // evaluates if there are enough TP available to mint, reverts if it's not
        _evalTPavailableToMint(i_, qTPtoMint, pACtp, ctargemaCA, lckAC, nACgain);
        if (qTPtoMint < qTPmin_) revert QtpBelowMinimumRequired(qTPmin_, qTPtoMint);

        // calculates qAC to be charged as fee
        // [N] = [N] * [PREC] / [PREC]
        qACfee = _mulPrec(qACtotalToRedeem, swapTCforTPFee);
        if (qACfee > qACmax_) revert InsufficientQacSent(qACmax_, qACfee);

        // inside a block to avoid stack too deep error
        {
            uint256 i = i_;
            uint256 qTC = qTC_;
            emit TCSwappedForTP(i, sender_, recipient_, qTC, qTPtoMint, qACfee);
        }

        _withdrawAndBurnTC(qTC_, 0, sender_);
        _depositAndMintTP(i_, qTPtoMint, 0, recipient_);

        // AC is only used to pay fees
        uint256 acChange = _onACNeededOperation(qACmax_, qACfee);
        // transfer any qAC change to the sender and distribute fees
        _distOpResults(sender_, acChange, qACfee);
        return (qACfee, qTPtoMint);
    }

    /**
     * @notice Allow redeem on liquidation state, user Peg balance gets burned and he receives
     * the equivalent AC given the liquidation frozen price.
     * @param i_ Pegged Token index
     * @param sender_ address owner of the TP to be redeemed
     * @param recipient_ address who receives the AC
     * @return qACRedeemed amount of AC sent to `recipient_`
     */
    function _liqRedeemTPTo(
        uint256 i_,
        address sender_,
        address recipient_
    ) internal notPaused returns (uint256 qACRedeemed) {
        if (!liquidated) revert OnlyWhenLiquidated();
        uint256 qTP = tpTokens[i_].balanceOf(sender_);
        if (qTP == 0) revert InsufficientTPtoRedeem(qTP, qTP);
        // [PREC]
        uint256 liqPACtp = tpLiqPrices[i_];
        // [PREC] = [N] * [PREC] / [PREC]
        qACRedeemed = _divPrec(qTP, liqPACtp);
        // burn qTP from the sender
        tpTokens[i_].burn(sender_, qTP);
        // Given rounding errors, the last redeemer might receive a little less
        if (acBalanceOf(address(this)) < qACRedeemed) qACRedeemed = acBalanceOf(address(this));
        emit TPRedeemed(i_, sender_, recipient_, qTP, qACRedeemed, 0);
        // transfer qAC to the recipient, reverts if fail
        acTransfer(recipient_, qACRedeemed);
    }

    /**
     * @notice Distributes Operation results to the different recipients
     * @param operatorsAddress_ operator's address to receive `operatorsQAC_`
     * @param operatorsQAC_ amount of AC to transfer operator [N]
     * @param qACfee_ amount of AC to be distributed as fees [N]
     */
    function _distOpResults(address operatorsAddress_, uint256 operatorsQAC_, uint256 qACfee_) internal {
        // [N] = [PREC] * [N] / [PREC]
        uint256 qACfeeRetained = _mulPrec(feeRetainer, qACfee_);
        // Increase collateral in the retain amount
        // TODO: review after issue #99 is completed
        nACcb += qACfeeRetained;
        // transfer qAC leftover fees to Fee Flow
        acTransfer(mocFeeFlowAddress, qACfee_ - qACfeeRetained);
        // transfer qAC to operator
        acTransfer(operatorsAddress_, operatorsQAC_);
    }

    // ------- Public Functions -------

    /**
     * @notice caller sends Collateral Token and receives Collateral Asset
     * @param qTC_ amount of Collateral Token to redeem
     * @param qACmin_ minimum amount of Collateral Asset that sender expects to receive
     * @return qACRedeemed amount of AC sent to sender
     */
    function redeemTC(uint256 qTC_, uint256 qACmin_) external returns (uint256 qACRedeemed) {
        return _redeemTCto(qTC_, qACmin_, msg.sender, msg.sender);
    }

    /**
     * @notice caller sends Collateral Token and recipient receives Collateral Asset
     * @param qTC_ amount of Collateral Token to redeem
